.article-content .highlight-box {
  position: relative;
  overflow: hidden;
  border-radius: 4px;
  margin: 1.6em 0;
  padding-top: 38px;
  background: #21252B;
}
.article-content .highlight::-webkit-scrollbar {
  height: 10px;
  background-color: #282C34;
}
.article-content .highlight::-webkit-scrollbar-track {
  border-radius: 10px;
  background-color: #282C34;
}

.article-content .highlight::-webkit-scrollbar-thumb {
  border-radius: 10px;
  background-color: #3E4451;
}
.article-content-cloud .highlight-box::before,
.article-content-mac .highlight-box::before {
  content: attr(data-rel);
  height: 38px;
  line-height: 38px;
  color: #fff;
  font-size: 14px;
  position: absolute;
  top: 0;
  left: 0;
  padding: 0px 75px;
}
.article-content-mac .highlight-box::before {
  padding: 0px 90px;
}
.article-content-cloud .highlight-box::after{
  content: "";
  position: absolute;
  top: 0;
  left: 4px;
  width: 46px;
  height: 19px;
  background-image: url('../images/icons/css_cloud.png')
}
.article-content-mac .highlight-box::after{
  content: " ";
  position: absolute;
  -webkit-border-radius: 50%;
  border-radius: 50%;
  background: #FF5454;
  width: 12px;
  height: 12px;
  top: 0;
  left: 20px;
  margin-top: 13px;
  -webkit-box-shadow: 20px 0px #FFBF00, 40px 0px #00D22E;
  box-shadow: 20px 0px #FFBF00, 40px 0px #00D22E;
  z-index: 3;
}
.article-content-doorframe .highlight-box {
  background-color: #282C34;
  border-radius: 0;
  border: 1px solid #be7e4a;
  box-shadow: 3px 0 #282C34, -3px 0 #282C34, 0px -3px #282C34, 0px 3px #282C34;
}
.article-content-doorframe .highlight-box::before,
.article-content-doorframe .highlight-box::after,
.article-content-doorframe .highlight-box .highlight::before,
.article-content-doorframe .highlight-box .highlight::after {
  content: "";
  display: block;
  position: absolute;
  background-image: url('../images/icons/css_doorframe.png');
  background-size: auto 44px;
  width: 44px;
  height: 44px;
  background-repeat: no-repeat;
}
.article-content-doorframe .highlight-box::before {
  top: 0;
  left: 0;
  transform:rotate(90deg);
}
.article-content-doorframe .highlight-box::after {
  top: 0;
  right: 0;
  transform:rotate(180deg);
}

.article-content-doorframe .highlight-box .highlight::before {
  bottom: 0;
  left: 0;
}
.article-content-doorframe .highlight-box .highlight::after {
  bottom: 0;
  right: 0;
  transform:rotate(-90deg);
}
.article-content-doorframe .highlight-box .highlight {
  padding-bottom: 53px !important;
}
.article-content .highlight {
  padding: 15px 20px;
}
.article-content pre,
.article-content .highlight {
  background: #282C34;
  overflow: auto;
  color: #ABB2BF;
  line-height: 22px;
}

.article-content .highlight .gutter pre,
.article-content .gist .gist-file .gist-data .line-numbers {
  color: #4B5363;
  font-size: 0.85em;
}
.article-content img {
  max-width: 100%;
}
.article-content pre,
.article-content code {
  font-family: "Source Code Pro", Consolas, Monaco, Menlo, Consolas, monospace;
}
.article-content code {
  background: #eee;
  text-shadow: 0 1px #fff;
  padding: 0 0.3em;
}
.article-content pre code {
  background: none;
  text-shadow: none;
  padding: 0;
}
.article-content .highlight pre {
  border: none;
  margin: 0;
  padding: 0;
}
.article-content .highlight table {
  margin: 0;
  width: auto;
}
.article-content .highlight td {
  border: none;
  padding: 0;
}
.article-content .highlight figcaption {
  font-size: 0.85em;
  color: #999;
  line-height: 1em;
  margin-bottom: 1em;
}
.article-content .highlight figcaption:before,
.article-content .highlight figcaption:after {
  content: "";
  display: table;
}
.article-content .highlight figcaption:after {
  clear: both;
}
.article-content .highlight figcaption a {
  float: right;
}
.article-content .highlight .gutter pre {
  text-align: right;
  padding-right: 20px;
}
.article-content .highlight .line {
  height: 22.400000000000002px;
}
.article-content .highlight .line.marked {
